import React from "react";
import { useQuery } from "@apollo/client";
import {
  GET_USER_PROFILE,
  GET_COMPETENCES,
  GET_PROJETS,
  GET_EXPERIENCES,
} from "../api/queries";

const Home = () => {
  const { loading: loadingProfil, error: errorProfil, data: dataProfil } = useQuery(GET_USER_PROFILE);
  const { data: dataCompetences } = useQuery(GET_COMPETENCES);
  const { data: dataProjets } = useQuery(GET_PROJETS);
  const { data: dataExperiences } = useQuery(GET_EXPERIENCES);

  if (loadingProfil) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
          <p className="mt-4 text-gray-600">Chargement du profil...</p>
        </div>
      </div>
    );
  }

  if (errorProfil) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="bg-white p-8 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold text-red-600 mb-2">Erreur</h2>
          <p className="text-gray-700">{errorProfil.message}</p>
        </div>
      </div>
    );
  }

  const profil = dataProfil?.getProfil || {};
  const competences = dataCompetences?.getCompetences || [];
  const projets = dataProjets?.getProjets || [];
  const experiences = dataExperiences?.getExperiences || [];

  return (
    <div className="bg-gray-50 min-h-screen py-12 px-4">
      <div className="max-w-6xl mx-auto">
        {/* Hero Section - Profil */}
        <div className="bg-gradient-to-r from-blue-600 to-blue-800 rounded-lg shadow-2xl p-8 mb-12 text-white">
          <div className="flex flex-col md:flex-row items-center gap-8">
            {profil.photo && (
              <img
                src={profil.photo}
                alt={profil.prenom}
                className="w-32 h-32 rounded-full object-cover border-4 border-white"
              />
            )}
            <div className="flex-1">
              <h1 className="text-4xl font-bold mb-2">
                {profil.prenom} {profil.nom}
              </h1>
              <p className="text-2xl text-blue-100 mb-4">{profil.titre}</p>
              <p className="text-blue-50 mb-4">{profil.bio}</p>
              <div className="flex flex-wrap gap-4 text-blue-100">
                {profil.email && (
                  <div className="flex items-center gap-2">
                    <span>üìß</span>
                    <a href={`mailto:${profil.email}`} className="hover:text-white">
                      {profil.email}
                    </a>
                  </div>
                )}
                {profil.telephone && (
                  <div className="flex items-center gap-2">
                    <span>üì±</span>
                    <a href={`tel:${profil.telephone}`} className="hover:text-white">
                      {profil.telephone}
                    </a>
                  </div>
                )}
                {profil.localisation && (
                  <div className="flex items-center gap-2">
                    <span>üìç</span>
                    <span className="hover:text-white">{profil.localisation}</span>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Comp√©tences */}
        <div className="bg-white rounded-lg shadow-lg p-8 mb-8">
          <h2 className="text-3xl font-bold text-gray-800 mb-6">üí° Comp√©tences</h2>
          {competences.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {competences.map((comp) => (
                <div
                  key={comp.id}
                  className="bg-gradient-to-br from-blue-50 to-blue-100 p-4 rounded-lg border-l-4 border-blue-500"
                >
                  <h3 className="font-semibold text-gray-800 mb-2">{comp.nom}</h3>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">{comp.categorie}</span>
                    <div className="flex gap-1">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <span
                          key={star}
                          className={`text-lg ${
                            star <= comp.niveau ? "text-yellow-400" : "text-gray-300"
                          }`}
                        >
                          ‚òÖ
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-500 text-center py-8">Aucune comp√©tence</p>
          )}
        </div>

