import { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { getToken } from '../utils/token';
import { login , logout  } from '../services/auth';
import client from '../apollo/client';
import { LOGIN_MUTATION } from '../api/mutations';

export const useAuth = () => {
    const { user, setUser } = useContext(AuthContext);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const token = getToken();
        // si token existe, tu peux décoder ou récupérer le user côté serveur
        // pour l'instant, on ne modifie pas setUser automatiquement
        setLoading(false);
    }, [setUser]);

    const login = async (email, password) => {
        setLoading(true);
        setError(null);
        try {
            // Utiliser Apollo client pour appeler la mutation GraphQL
            const result = await client.mutate({
                mutation: LOGIN_MUTATION,
                variables: { email, password },
            });
            const payload = result?.data?.login;
            if (!payload || !payload.token) {
                throw new Error('No token returned from login mutation');
            }
            // sauvegarder le token et le user
            // setToken(payload.token);
            setUser(payload.user || null);
            return payload;
        } catch (err) {
            console.error('useAuth.login error', err);
            setError(err);
            throw err;
        } finally {
            setLoading(false);
        }
    };

    const logout = () => {
        logoutService();
        setUser(null);
    };

    const isAuthenticated = !!getToken();

    return { user, setUser, login, logout, loading, error, isAuthenticated };
};

export default useAuth;
