import { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { getToken, setToken } from '../utils/token';
import { logout as logoutService } from '../services/auth';
import client from '../apollo/client';
import { LOGIN_MUTATION } from '../api/mutations';

export const useAuth = () => {
    const { user, setUser } = useContext(AuthContext);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const token = getToken();
        if (token) {
            // optionnel : décoder token ou récupérer user
        }
        setLoading(false);
    }, [setUser]);

    const login = async (username, email, password) => {
        setLoading(true);
        setError(null);
        try {e
            const result = await client.mutate({
                mutation: LOGIN_MUTATION,
                variables: {mail, password },
            });
            const payload = result?.data?.login;
            if (!payload || !payload.token) {
                const msg = result?.errors?.[0]?.message || 'No token returned';
                throw new Error(msg);
            }
            setToken(payload.token); // sauvegarde du token
            setUser(payload.user || null);
            return payload;
        } catch (err) {
            console.error('useAuth.login error', err);
            setError(err);
            throw err;
        } finally {
            setLoading(false);
        }
    };

    const logout = () => {
        logoutService();
        setUser(null);
    };

    const isAuthenticated = !!getToken();

    return { user, setUser, login, logout, loading, error, isAuthenticated };
};

export default useAuth;
