import React, { useState } from "react";
import { useMutation, useQuery } from "@apollo/client";
import {
  CREATE_PROJET,
  UPDATE_PROJET,
  DELETE_PROJET,
} from "../../api/mutations";
import { GET_USER_PROFILE } from "../../api/queries";

const ProjetsManager = () => {
  const [showForm, setShowForm] = useState(false);
  const [editId, setEditId] = useState(null);
  const [formData, setFormData] = useState({
    titre: "",
    description: "",
    lien: "",
    dateDebut: "",
    dateFin: "",
    competences: [],
  });

  const { refetch } = useQuery(GET_USER_PROFILE);

  const [createProjet] = useMutation(CREATE_PROJET, {
    onCompleted: () => refetch(),
  });
  const [updateProjet] = useMutation(UPDATE_PROJET, {
    onCompleted: () => refetch(),
  });
  const [deleteProjet] = useMutation(DELETE_PROJET, {
    onCompleted: () => refetch(),
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const input = formData;
      if (editId) {
        await updateProjet({
          variables: { id: editId, input },
        });
        setEditId(null);
      } else {
        await createProjet({
          variables: { input },
        });
      }
      setFormData({
        titre: "",
        description: "",
        lien: "",
        dateDebut: "",
        dateFin: "",
        competences: [],
      });
      setShowForm(false);
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  const handleEdit = (proj) => {
    setEditId(proj.id);
    setFormData({
      titre: proj.titre,
      description: proj.description,
      technologies: Array.isArray(proj.technologies)
        ? proj.technologies.join(", ")
        : "",
      image: proj.image || "",
      dateDebut: proj.dateDebut,
      dateFin: proj.dateFin || "",
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm("√ätes-vous s√ªr ?")) {
      try {
        await deleteProjet({ variables: { id } });
      } catch (error) {
        console.error("Erreur:", error);
      }
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-8 mb-8">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">üöÄ G√©rer Projets</h2>
        <button
          onClick={() => {
            setShowForm(!showForm);
            setEditId(null);
            setFormData({
              titre: "",
              description: "",
              technologies: "",
              image: "",
              dateDebut: "",
              dateFin: "",
            });
          }}
          className="bg-blue-600 text-white px-4 py-2 rounded font-semibold hover:bg-blue-700"
        >
          {showForm ? "Annuler" : "+ Ajouter"}
        </button>
      </div>

      {showForm && (
        <form
          onSubmit={handleSubmit}
          className="mb-6 p-4 bg-gray-50 rounded-lg space-y-4"
        >
          <input
            type="text"
            placeholder="Titre du projet"
            value={formData.titre}
            onChange={(e) =>
              setFormData({ ...formData, titre: e.target.value })
            }
            required
            className="w-full border rounded px-3 py-2"
          />
          <textarea
            placeholder="Description"
            value={formData.description}
            onChange={(e) =>
              setFormData({ ...formData, description: e.target.value })
            }
            rows="3"
            className="w-full border rounded px-3 py-2"
          />
          <input
            type="text"
            placeholder="Technologies (s√©par√©es par des virgules)"
            value={formData.technologies}
            onChange={(e) =>
              setFormData({ ...formData, technologies: e.target.value })
            }
            className="w-full border rounded px-3 py-2"
          />
          <input
            type="url"
            placeholder="URL de l'image"
            value={formData.image}
            onChange={(e) =>
              setFormData({ ...formData, image: e.target.value })
            }
            className="w-full border rounded px-3 py-2"
          />
          <div className="grid grid-cols-2 gap-4">
            <input
              type="date"
              value={formData.dateDebut}
              onChange={(e) =>
                setFormData({ ...formData, dateDebut: e.target.value })
              }
              className="border rounded px-3 py-2"
            />
            <input
              type="date"
              value={formData.dateFin}
              onChange={(e) =>
                setFormData({ ...formData, dateFin: e.target.value })
              }
              className="border rounded px-3 py-2"
            />
          </div>
          <button
            type="submit"
            className="bg-green-600 text-white px-4 py-2 rounded font-semibold hover:bg-green-700"
          >
            {editId ? "Mettre √† jour" : "Cr√©er"}
          </button>
        </form>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Les projets s'affichent ici */}
        <p className="text-gray-500">
          Les projets seront g√©r√©s depuis le dashboard
        </p>
      </div>
    </div>
  );
};

export default ProjetsManager;
