import React from "react";
import { useQuery } from "@apollo/client";
import { GET_USER_PROFILE } from "../api/queries";
import { useAuth } from "../hooks/useAuth";

const Dashboard = () => {
  const { user } = useAuth();

  const { loading, error, data } = useQuery(GET_USER_PROFILE, {
    fetchPolicy: "network-only",
  });

  console.debug("useAuth user:", user);
  console.debug("GET_USER_PROFILE data:", data, "error:", error);

  if (loading) return <div>Loading...</div>;
  if (error) {
    console.error("GraphQLError details:", {
      message: error.message,
      graphQLErrors: error.graphQLErrors,
      networkError: error.networkError,
    });
    return <div>Error: {error.message}</div>;
  }

  // utiliser data.me (query retourne `me`)
  const profile = data?.user || user || {};

  const displayName =
    profile.name || profile.username || profile.email || "Utilisateur";

  return (
    <div className="dashboard">
      <h1>Welcome, {displayName}</h1>
      <h2>Your Data:</h2>
      <pre>{JSON.stringify(profile, null, 2)}</pre>
    </div>
  );
};

export default Dashboard;
