import { gql } from "@apollo/client";

export const GET_USER_PROFILE = gql`
  query GetUserProfile {
    user {
      id
      name
      email
      bio
      avatar
    }
  }
`;

export const GET_USER_PROJECTS = gql`
  query GetUserProjects {
    projects {
      id
      title
      description
      url
      createdAt
    }
  }
`;

export const GET_ALL_PROJECTS = gql`
  query GetAllProjects {
    allProjects {
      id
      title
      description
      url
      createdAt
      owner {
        id
        name
      }
    }
  }
`;
