import React from "react";
import { useQuery } from "@apollo/client";
import { GET_USER_PROFILE } from "../api/queries";
import { useAuth } from "../hooks/useAuth";

const Dashboard = () => {
  const { user } = useAuth();

  const { loading, error, data } = useQuery(GET_USER_PROFILE, {
    fetchPolicy: "network-only",
  });

  console.debug("useAuth user:", user);
  console.debug("GET_USER_PROFILE data:", data, "error:", error);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
          <p className="mt-4 text-gray-600">Chargement...</p>
        </div>
      </div>
    );
  }

  if (error) {
    console.error("GraphQLError details:", {
      message: error.message,
      graphQLErrors: error.graphQLErrors,
      networkError: error.networkError,
    });
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <div className="bg-white p-8 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold text-red-600 mb-2">Erreur</h2>
          <p className="text-gray-700">{error.message}</p>
        </div>
      </div>
    );
  }

  const profil = data?.getProfil || {};
  const competences = data?.getCompetences || [];
  const projets = data?.getProjets || [];
  const experiences = data?.getExperiences || [];

  const displayName =
    `${profil.prenom || ""} ${profil.nom || ""}`.trim() || "Utilisateur";

  return (
    <div className="bg-gray-50 min-h-screen py-12 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header Profil */}
        <div className="bg-white rounded-lg shadow-lg p-8 mb-8">
          <div className="flex items-start gap-6">
            {profil.photo ? (
              <img
                src={profil.photo}
                alt={displayName}
                className="w-24 h-24 rounded-full object-cover"
              />
            ) : (
              <div className="w-24 h-24 rounded-full bg-blue-500 flex items-center justify-center text-white text-3xl font-bold">
                {profil.prenom?.[0]}
                {profil.nom?.[0]}
              </div>
            )}
            <div className="flex-1">
              <h1 className="text-4xl font-bold text-gray-800 mb-2">
                {displayName}
              </h1>
              <p className="text-xl text-blue-600 font-semibold mb-3">
                {profil.titre}
              </p>
              <p className="text-gray-600 text-lg mb-4">{profil.bio}</p>
              <div className="flex gap-4 flex-wrap">
                {profil.email && (
                  <div className="flex items-center gap-2">
                    <span className="text-gray-600 font-semibold">Email:</span>
                    <a
                      href={`mailto:${profil.email}`}
                      className="text-blue-600 hover:underline"
                    >
                      {profil.email}
                    </a>
                  </div>
                )}
                {profil.telephone && (
                  <div className="flex items-center gap-2">
                    <span className="text-gray-600 font-semibold">T√©l:</span>
                    <a
                      href={`tel:${profil.telephone}`}
                      className="text-blue-600 hover:underline"
                    >
                      {profil.telephone}
                    </a>
                  </div>
                )}
              </div>
              {profil.localisation && (
                <p className="text-gray-600 mt-3">üìç {profil.localisation}</p>
              )}
              {/* R√©seaux Sociaux */}
              {profil.reseauxSociaux && (
                <div className="flex gap-4 mt-4">
                  {profil.reseauxSociaux.github && (
                    <a
                      href={profil.reseauxSociaux.github}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-gray-600 hover:text-gray-800 font-semibold"
                    >
                      GitHub
                    </a>
                  )}
                  {profil.reseauxSociaux.linkedin && (
                    <a
                      href={profil.reseauxSociaux.linkedin}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-600 hover:text-blue-800 font-semibold"
                    >
                      LinkedIn
                    </a>
                  )}
                  {profil.reseauxSociaux.portfolio && (
                    <a
                      href={profil.reseauxSociaux.portfolio}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-purple-600 hover:text-purple-800 font-semibold"
                    >
                      Portfolio
                    </a>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Grille 2 colonnes */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
