import React from "react";
import { useQuery } from "@apollo/client";
import { GET_USER_PROFILE } from "../api/queries";
import { useAuth } from "../hooks/useAuth";

const Dashboard = () => {
  const { user } = useAuth();

  // Ne pas skip la query si tu veux toujours récupérer le profil du serveur
  const { loading, error, data } = useQuery(GET_USER_PROFILE, {
    fetchPolicy: "network-only", // force fresh data
  });

  console.debug("useAuth user:", user);
  console.debug("GET_USER_PROFILE data:", data, "error:", error);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;

  // profile peut venir de data.user (GraphQL) ou du user local (auth)
  const profile = data?.user || user || {};

  // gérer plusieurs noms possibles renvoyés par le backend
  const displayName = profile.name || profile.username || profile.email || "Utilisateur";

  return (
    <div className="dashboard">
      <h1>Welcome, {displayName}</h1>
      <h2>Your Data:</h2>
      <pre>{JSON.stringify(profile, null, 2)}</pre>
    </div>
  );
};

export default Dashboard;
