import { gql } from "@apollo/client";

export const GET_USER_PROFILE = gql`
  query GetUserProfile {
    getProfil {
      id
      nom
      prenom
      titre
      bio
      email
      telephone
      localisation
      photo
      reseauxSociaux {
        github
        linkedin
        twitter
        portfolio
      }
    }
    getCompetences {
      id
      nom
      niveau
      categorie
    }
    getProjets {
      id
      titre
      description
      lien
      dateDebut
      dateFin
      competences {
        id
        nom
      }
    }
    getExperiences {
      id
      titre
      entreprise
      description
      dateDebut
      dateFin
      competences {
        id
        nom
      }
    }
  }
`;

export const GET_USER_PROJECTS = gql`
  query GetUserProjects {
    projects {
      id
      title
      description
      url
      createdAt
    }
  }
`;

export const GET_ALL_PROJECTS = gql`
  query GetAllProjects {
    allProjects {
      id
      title
      description
      url
      createdAt
      owner {
        id
        name
      }
    }
  }
`;
