import React, { useState } from "react";
import { useMutation, useQuery } from "@apollo/client";
import {
  CREATE_EXPERIENCE,
  UPDATE_EXPERIENCE,
  DELETE_EXPERIENCE,
} from "../../api/mutations";
import { GET_EXPERIENCES } from "../../api/queries";

const ExperiencesManager = () => {
  const [showForm, setShowForm] = useState(false);
  const [editId, setEditId] = useState(null);
  const [formData, setFormData] = useState({
    titre: "",
    entreprise: "",
    description: "",
    dateDebut: "",
    dateFin: "",
    enCours: false,
    competences: [],
  });

  const { refetch } = useQuery(GET_USER_PROFILE);

  const [createExperience] = useMutation(CREATE_EXPERIENCE, {
    onCompleted: () => refetch(),
  });
  const [updateExperience] = useMutation(UPDATE_EXPERIENCE, {
    onCompleted: () => refetch(),
  });
  const [deleteExperience] = useMutation(DELETE_EXPERIENCE, {
    onCompleted: () => refetch(),
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await updateExperience({
          variables: { id: editId, input: formData },
        });
        setEditId(null);
      } else {
        await createExperience({
          variables: { input: formData },
        });
      }
      setFormData({
        titre: "",
        entreprise: "",
        description: "",
        dateDebut: "",
        dateFin: "",
        enCours: false,
        competences: [],
      });
      setShowForm(false);
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  const handleEdit = (exp) => {
    setEditId(exp.id);
    setFormData({
      poste: exp.poste,
      entreprise: exp.entreprise,
      description: exp.description || "",
      dateDebut: exp.dateDebut,
      dateFin: exp.dateFin || "",
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm("√ätes-vous s√ªr ?")) {
      try {
        await deleteExperience({ variables: { id } });
      } catch (error) {
        console.error("Erreur:", error);
      }
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-8 mb-8">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">
          üíº G√©rer Exp√©riences
        </h2>
        <button
          onClick={() => {
            setShowForm(!showForm);
            setEditId(null);
            setFormData({
              titre: "",
              entreprise: "",
              description: "",
              dateDebut: "",
              dateFin: "",
              enCours: false,
              competences: [],
            });
          }}
          className="bg-blue-600 text-white px-4 py-2 rounded font-semibold hover:bg-blue-700"
        >
          {showForm ? "Annuler" : "+ Ajouter"}
        </button>
      </div>

      {showForm && (
        <form
          onSubmit={handleSubmit}
          className="mb-6 p-4 bg-gray-50 rounded-lg space-y-4"
        >
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input
              type="text"
              placeholder="Titre du poste"
              value={formData.titre}
              onChange={(e) =>
                setFormData({ ...formData, titre: e.target.value })
              }
              required
              className="border rounded px-3 py-2"
            />
            <input
              type="text"
              placeholder="Entreprise"
              value={formData.entreprise}
              onChange={(e) =>
                setFormData({ ...formData, entreprise: e.target.value })
              }
              className="border rounded px-3 py-2"
            />
          </div>
          <textarea
            placeholder="Description"
            value={formData.description}
            onChange={(e) =>
              setFormData({ ...formData, description: e.target.value })
            }
            rows="3"
            className="w-full border rounded px-3 py-2"
          />
          <div className="grid grid-cols-2 gap-4">
            <input
              type="date"
              value={formData.dateDebut}
              onChange={(e) =>
                setFormData({ ...formData, dateDebut: e.target.value })
              }
              className="border rounded px-3 py-2"
            />
            <input
              type="date"
              value={formData.dateFin}
              onChange={(e) =>
                setFormData({ ...formData, dateFin: e.target.value })
              }
              className="border rounded px-3 py-2"
            />
          </div>
          <button
            type="submit"
            className="bg-green-600 text-white px-4 py-2 rounded font-semibold hover:bg-green-700"
          >
            {editId ? "Mettre √† jour" : "Cr√©er"}
          </button>
        </form>
      )}

      <div className="space-y-2">
        <p className="text-gray-500 text-center">
          Les exp√©riences seront g√©r√©es depuis le dashboard
        </p>
      </div>
    </div>
  );
};

export default ExperiencesManager;
